import { Interval } from '../interval';
/**
 * Division between intervals when `y` doesn't contain zero
 * @param {Interval} x
 * @param {Interval} y
 * @returns {Interval}
 */
export declare function nonZero(x: Interval, y: Interval): Interval;
/**
 * Division between an interval and a positive constant
 * @param {Interval} x
 * @param {number} v
 * @returns {Interval}
 */
export declare function positive(x: Interval, v: number): Interval;
/**
 * Division between an interval and a negative constant
 * @param {Interval} x
 * @param {number} v
 * @returns {Interval}
 */
export declare function negative(x: Interval, v: number): Interval;
/**
 * Division between an interval and zero
 * @param {Interval} x
 * @returns {Interval}
 */
export declare function zero(x: Interval): Interval;
